$(function () {
    let socket = io();
    let cln = '';
    let cln_temp = '';
    let pss = 'left';
    $('.chat').niceScroll();
    
    function addMessage (dt) {
        var template = '<div class="answer '+ dt.pss +'" id="msgUser">' +
            '<div class="avatar"><img src="'+ dt.avatar +'" alt=""></div>' + dt.name +
            '<div class="text">'+ dt.text + '</div>' +
            '</div>';

        $('#chat-messages').append(template);
        $('.chat').niceScroll();
    }

    function beep() {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }
    
    let avatar = 'img/avatar.png';

    /* ====================================================================== */
    $(document).on('click', '#sendName', function () {
        let name = $('#addName').val();
        cln = name;
        $('#pageStart').remove();
        $('#pageMessage').show();

        socket.emit('send-add', name);
        return false;
    });
    $(document).on('keypress', '#addName', function(e) {
        if(e.which == 13) {
			let name = $(this).val();
            cln = name;
			$('#pageStart').remove();
			$('#pageMessage').show();

			socket.emit('send-add', name);
			return false;
        }
    });
    socket.on('trim-add', function (msg) {
        let idx = msg.idx;
        addMessage({
            pss: (idx === cln ? 'right' : 'left'),
            avatar: avatar,
            name: idx,
            text: idx + ' Joined',
            online: true,
            num: msg.num
        });
    });

    $(document).on('click', '#SendMessage', function () {
        socket.emit('send-msg', {
            idx: cln,
            text: $('#textMessage').val()
        });

        $('#textMessage').val('');
        return false;
    });
    $(document).on('keypress', '#textMessage', function(e) {
        if(e.which == 13) {
            socket.emit('send-msg', {
                idx: cln,
                text: $('#textMessage').val()
            });

            $('#textMessage').val('');
            return false;
        }
    });
    socket.on('trim-msg', function (msg) {
        var pss = (msg.idx === cln ? 'right' : 'left');

        if($('.answer.right').length < 1){
            $('div#chat-messages').append(addMessage({
                pss: pss,
                avatar: (msg.num === 1 ? avatar : ''),
                name: (msg.num === 1 ? msg.idx : ''),
                text: msg.text,
                online: (msg.num === 1 ? true : false),
                num: msg.num
            }));
        } else {
            $('div#chat-messages').append(addMessage({
                pss: pss,
                avatar: (msg.num === 1 ? avatar : ''),
                name: (msg.num === 1 ? msg.idx : ''),
                text: msg.text,
                online: false,
                num: msg.num
            }));
        }

        beep();
    });
});